import { Component } from "@angular/core";

@Component({
  selector: 'app-boton-sesion',
  templateUrl: './boton-sesion.component.html',
  styleUrls: ['./boton-sesion.component.css']
})

export class BotonSesionComponent {

  sesion: boolean = false;
  usuarios: Usuario[] = [];
  usuarioActual!: Usuario;
  sesionIniciada: boolean = false;

  nombre: string = "";
  apellido: string = "";
  correo: string = "";
  telefono: string = "";
  contrasena: string = "";
  nacimiento: Date = new Date;

  /*constructor() {
    this.usuarioActual = JSON.parse(localStorage.getItem("usuarioActual") || "");
    if (this.usuarioActual == null) {
      this.sesionIniciada = false;
      this.usuarios = JSON.parse(localStorage.getItem("usuarios") || "");
    } else {
      this.sesionIniciada = true;
    }
  }*/

  regitrarUsuario(): void {
    this.usuarioActual.nombre = this.nombre;
    this.usuarioActual.apellido = this.apellido;
    this.usuarioActual.correo = this.correo;
    this.usuarioActual.contrasena = this.contrasena;
    this.usuarioActual.telefono = this.telefono;
    this.usuarioActual.nacimiento = this.nacimiento;
    console.log(this.usuarioActual);
  }
}

interface Usuario {
  nombre: string,
  apellido: string,
  correo: string,
  telefono: string,
  contrasena: string,
  nacimiento: Date,
}
