import { Component } from "@angular/core";

@Component({
  selector: 'app-boton-sesion',
  templateUrl: './boton-sesion.component.html',
  styleUrls: ['./boton-sesion.component.css']
})

export class BotonSesionComponent {

  sesion: boolean = false;
  usuarios: Usuario[] = [];
  usuarioActual!: Usuario;
  sesionIniciada: boolean = false;
  error: boolean = false;
  msjError: string = "";

  nombre: string = "";
  apellido: string = "";
  correo: string = "";
  telefono: string = "";
  contrasena: string = "";
  nacimiento: Date = new Date;

  constructor() {
    if (localStorage.getItem("usuarios") != null) {
      this.usuarios = JSON.parse(localStorage.getItem("usuarios") || "");
    }
    if (localStorage.getItem("usuarioActual") != null) {
      this.usuarioActual = JSON.parse(localStorage.getItem("usuarioActual") || "");
      this.sesionIniciada = false;
    } else {
      this.sesionIniciada = true;
    }
  }

  registrarUsuario() {
    let aux: Usuario = {
      nombre: this.nombre,
      apellido: this.apellido,
      correo: this.correo,
      telefono: this.telefono,
      contrasena: this.contrasena,
      nacimiento: this.nacimiento,
    }
    if (this.validarRegistro(aux)) {
      this.usuarioActual = aux;
      localStorage.setItem("usuarioActual", JSON.stringify(this.usuarioActual));
      this.usuarios.push(aux);
      localStorage.removeItem("usuarios");
      localStorage.setItem("usuarios", JSON.stringify(this.usuarios));
    }
  }

  validarRegistro(usuario: Usuario): boolean {
    for (let i of this.usuarios) {
      if (i.correo == usuario.correo) {
        this.error = true;
        this.msjError = "Esta cuenta de correo ya esta siendo usada por un usuario.";
        return false;
      }
      if (i.telefono == usuario.telefono) {
        this.error = true;
        this.msjError = "Este numero de telefono ya esta siendo usado por un usuario.";
        return false;
      }
    }
    return true
  }

  limpiar(): void {
    this.nombre = "";
    this.apellido = "";
    this.correo = "";
    this.telefono = "";
    this.contrasena = "";
    this.nacimiento = new Date;
  }
}

interface Usuario {
  nombre: string,
  apellido: string,
  correo: string,
  telefono: string,
  contrasena: string,
  nacimiento: Date,
}
